          columns:{
            #"SYSTEM_SYSRULES_gridTextColumn"(label: "User", data: index(local!datasubset.data, "user", null), field: "user"),
            #"SYSTEM_SYSRULES_gridTextColumn"(label: "Client", data: index(local!datasubset.data, "clientType", null), field: "clientType"),
            #"SYSTEM_SYSRULES_gridTextColumn"(label: "Time", data: index(local!datasubset.data, "lastRequestTs", null), field: "lastRequestTs")
          },
          value: local!pagingInfo,
          saveInto: local!pagingInfo,
          totalCount: count(local!datasubset.data)
        )
      }
    )
  )
)
