rule.definition:
with(
  local!setAndIncreaseIfNeeded: lambda_appian_internal(
    ri!fieldValue,
    if(   /* Do we need to add? */
      and(
        not(isnull(ri!fieldValue)),                 /* If the field isn't null (something has been added here) */
        index = count(ri!files)                 /* and we're at the end of the list */
      ),
      updatearray(append(ri!files, null), index, ri!fieldValue),  /* Update the list with the new value and add a null to the end */
      if( /* Do we need to remove? */
        and(
          isnull(ri!fieldValue),                        /* Field is now null (value was removed) */
          count(ri!files) > 1                       /* We aren't down to just one field yet */
        ),
        remove(ri!files, ri!index),                 /* Remove the list item for this field */
        updatearray(ri!files, ri!index, ri!fieldValue) /* Neither add nor remove, just update the list with the new value */
      )
    )
  ),
  #"SYSTEM_SYSRULES_fileUploadField"(
    label: if(ri!index = 1, "Attachment", ""),
    target: #"_a-0000d825-eb59-8000-e787-010000010000_1266",
    value: ri!files[ri!index],
    saveInto: {
      ri!files << local!setAndIncreaseIfNeeded(_)
    }
  )
)
