rule.definition:
load(
  local!state,
  local!list: { 11, 22, 33 },
  local!list2: { 11, 22, 33 },
  local!swap: lambda_appian_internal(
    ri!array, ri!a, ri!b,
    with(
      local!temp: ri!array[ri!a],
      updatearray(updatearray(ri!array, ri!a, ri!array[ri!b]), ri!b, local!temp)
    )
  ),
  local!renderItemFn: lambda_appian_internal(
    ri!index,
    #"SYSTEM_SYSRULES_integerField"(label: "Input " & ri!index, value: local!list[ri!index], saveInto: local!list[ri!index])
  ),
  local!renderItemFn2: lambda_appian_internal(
    ri!index,
    #"SYSTEM_SYSRULES_integerField"(label: "Input " & ri!index, value: local!list2[ri!index], saveInto: local!list2[ri!index])
  ),
  #"SYSTEM_SYSRULES_dashboardLayout"(firstColumnContents: {
    #"SYSTEM_SYSRULES_sectionLayout"(label: "Without Preservation", firstColumnContents: {
      #"SYSTEM_SYSRULES_applyComponents"(function: local!renderItemFn(_), array: 1 + enumerate(count(local!list))),
      #"SYSTEM_SYSRULES_ButtonArrayLayout"(buttons: {
        #"SYSTEM_SYSRULES_ButtonWidget"(label: "Add Row", value: " ", saveInto: local!list << append(local!list, _)),
        #"SYSTEM_SYSRULES_ButtonWidget"(
          label: "Swap Rows 2 and 3",
          value: 2,
          saveInto: {
            local!list << local!swap(local!list, _, 3)
          }
        )
      })
    }),
    #"SYSTEM_SYSRULES_sectionLayout"(label: "With Preservation", firstColumnContents: {
      #"SYSTEM_SYSRULES_applyComponents"(function: local!renderItemFn2(_), array: 1 + enumerate(count(local!list2)), arrayVariable: local!state),
      #"SYSTEM_SYSRULES_textField"(instructions: typename(typeof(local!state)) & " (" & typename(runtimetypeof(local!state)) & ")", readOnly: true, value: local!state),
      #"SYSTEM_SYSRULES_ButtonArrayLayout"(buttons: {
        #"SYSTEM_SYSRULES_ButtonWidget"(label: "Add Row", value: " ", saveInto: local!list2 << append(local!list2, _)),
        #"SYSTEM_SYSRULES_ButtonWidget"(
          label: "Swap Rows 2 and 3",
          value: 2,
          saveInto: {
            local!list2 << local!swap(local!list2, _, 3),
            local!state << local!swap(local!state, _, 3)
          }
        )
      })
    })
  })
)
