rule.definition:
fn!load(
  local!group,
  local!save,
  local!hideUsers,
  local!readOnly,
  local!selectionType: "ANY",
  local!path,
  local!selectionValue,
  #"SYSTEM_SYSRULES_dashboardLayout"(
    firstColumnContents:{
      #"SYSTEM_SYSRULES_sectionLayout"(
        label:"Controls",
        firstColumnContents:{
          #"SYSTEM_SYSRULES_pickerFieldGroups"(
            label: "Initial Group",
            labelPosition: "ADJACENT",
            maxSelections: 1,
            value: local!group,
            saveInto: {
              local!group,
              a!save(local!path, {})
            }
          ),
          #"SYSTEM_SYSRULES_radioButtonField"(
            label: "Browser Type",
            labelPosition: "ADJACENT",
            choiceLabels: {"User", "Group", "Any"},
            choiceValues: {"USER", "GROUP", "ANY"},
            value: local!selectionType,
            saveInto: {
              local!selectionType,
              a!save(local!selectionValue, fn!null()),
              a!save(local!path, fn!null())
            },
            choiceLayout: "COMPACT"
          )
        },
        secondColumnContents: {
          #"SYSTEM_SYSRULES_checkboxField"(
            labelPosition: "COLLAPSED",
            choiceLabels: {"Read-only"},
            choiceValues: {true},
            value: local!readOnly,
            saveInto: local!readOnly
          ),
          fn!if(local!selectionType = "GROUP",
            #"SYSTEM_SYSRULES_checkboxField"(
              labelPosition: "COLLAPSED",
              choiceLabels: {"Hide users"},
              choiceValues: { fn!true() },
              value: local!hideUsers,
              saveInto: local!hideUsers
            ),
            {}
          )
        }
      ),
      fn!if(
        #"SYSTEM_SYSRULES_isNullOrEmpty"(local!group),
        {},
        {
          #"SYSTEM_SYSRULES_sectionLayout"(
            label:"Component",
            firstColumnContents: {
              fn!choose(fn!wherecontains(local!selectionType, {"USER", "GROUP", "ANY"})[1],
                #"SYSTEM_SYSRULES_userBrowserFieldColumns"(
                  label: "User Browser",
                  labelPosition: "ADJACENT",
                  instructions: "instructions",
                  height: "MEDIUM",
                  rootGroup: local!group,
                  pathValue: local!path,
                  pathSaveInto: local!path,
                  selectionValue: local!selectionValue,
                  selectionSaveInto: local!selectionValue,
                  readOnly: local!readOnly
                ),
                #"SYSTEM_SYSRULES_groupBrowserFieldColumns"(
                  label: "Group Browser",
                  labelPosition: "ADJACENT",
                  instructions: "instructions",
                  height: "MEDIUM",
                  rootGroup: local!group,
                  pathValue: local!path,
                  pathSaveInto: local!path,
                  selectionValue: local!selectionValue,
                  selectionSaveInto: local!selectionValue,
                  readOnly: local!readOnly,
                  hideUsers: local!hideUsers
                ),
                #"SYSTEM_SYSRULES_userAndGroupBrowserFieldColumns"(
                  label: "User and Group Browser",
                  labelPosition: "ADJACENT",
                  instructions: "instructions",
                  height: "MEDIUM",
                  rootGroup: local!group,
                  pathValue: local!path,
                  pathSaveInto: local!path,
                  selectionValue: local!selectionValue,
                  selectionSaveInto: local!selectionValue,
                  readOnly: local!readOnly
                )
              )
            }
          ),
          fn!if(#"SYSTEM_SYSRULES_isNullOrEmpty"(local!selectionValue),
            {},
            #"SYSTEM_SYSRULES_sectionLayout"(
              label: "Selected",
              firstColumnContents: {
                #"SYSTEM_SYSRULES_textField"(
                  label: "Path",
                  labelPosition: "ADJACENT",
                  value: local!path,
                  readOnly:true
                ),
                #"SYSTEM_SYSRULES_textField"(
                  label: "Selection",
                  labelPosition: "ADJACENT",
                  value: local!selectionValue,
                  readOnly: true
                )
              }
            )
          )
        }
      )
    }
  )
)
